mapper.select:
SELECT pname, property_id, NULL AS property_service_id, p_service_type, NULL AS ps_service_type, price, property_type_id, NULL AS private_property FROM property WHERE pname LIKE CONCAT('%', #{keyword}, '%')
UNION ALL
SELECT pname, NULL AS property_id, property_service_id, NULL AS p_service_type, ps_service_type, price, property_type_id, private_property FROM property_service WHERE pname LIKE CONCAT('%', #{keyword}, '%');
